unsigned long binomial(unsigned long n, unsigned long k) {
  unsigned long c = 1, i;

  if (k > n-k) // take advantage of symmetry
    k = n-k;

  for (i = 1; i <= k; i++, n--) {
    if (c/i > UINT_MAX/n) // return 0 on overflow
      return 0;

    c = c / i * n + c % i * n / i;  // split c * n / i into (c / i * i + c% i) * n / i
  }

  return c;
}

